mapper.select:
/*selectNoticeList*/ SELECT notice_idx AS noticeIdx, notice_title AS noticeTitle, notice_cont AS noticeContent, ( CASE notice_open_yn WHEN 'Y' THEN '공개' WHEN 'N' THEN '비공개' END ) AS noticeOpenYn, date_format(reg_dt, '%Y.%m.%d') AS noticeRegDt FROM ta_notice WHERE 1=1
AND notice_title LIKE CONCAT('%',#{searchText},'%') OR notice_open_yn LIKE CONCAT('%',#{searchText},'%')
AND notice_title LIKE CONCAT('%',#{searchText},'%')
ORDER BY notice_idx ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO ta_notice (
	 notice_idx
	,notice_title
	,notice_cont
	,notice_open_yn
	,notice_impt_yn
	,file_course
	,org_file_nm
	,upload_file_nm
	,reg_dt
	,reg_id
)
VALUES(
	 (select concat('N',IFNULL(LPAD(MAX(SUBSTRING(notice_idx,2,LENGTH(notice_idx))) + 1,4,'0'),'0000')) from ta_notice a)
	,#{noticeTitle}
	,#{noticeCont}
	,#{noticeOpenYn}
	,#{noticeImptYn}
	,#{fileCourse}
	,#{orgFileNm}
	,#{uploadFileNm}
	,CURRENT_TIMESTAMP
	,'admin'
)

mapper.update:
UPDATE ta_notice
SET 
	org_file_nm = #{noticeFile}
	where notice_idx = #{noticeIdx}
ORDER BY notice_idx DESC LIMIT 1

mapper.insert:
INSERT INTO ta_faq (
	 faq_idx
	,faq_title  
	,faq_cont
	,faq_open_yn
	,file_course
	,org_file_nm
	,upload_file_nm
	,reg_dt
	,reg_id
)
VALUES(
(select concat('F',IFNULL(LPAD(MAX(SUBSTRING(faq_idx,2,LENGTH(faq_idx))) + 1,4,'0'),'0000')) from ta_faq a)
	,#{faq_title}
	,#{faq_cont}
	,#{faq_open_yn}
	,#{fileCourse}
	,#{orgFileNm}
	,#{uploadFileNm}
	,CURRENT_TIMESTAMP
	,'admin'
)

mapper.update:
UPDATE ta_faq 
	set
	 faq_idx = #{faq_idx}, 
	 faq_title = #{faq_title}, 
	 faq_cont = #{faq_cont}, 
	 faq_open_yn = #{faq_open_yn}
	 ,file_course =  #{fileCourse}
	 ,org_file_nm = #{orgFileNm}
where faq_idx = #{faq_idx}

UPDATE ta_faq
SET 
	org_file_nm = #{faqFile}
	where faq_idx = #{faqIdx}
ORDER BY faq_idx DESC LIMIT 1
