mapper.insert:
INSERT INTO tb_member (
	 member_idx
	,member_id
	,member_pw
	,member_nm
	,member_addr
	,member_tel
	,member_hp
	,member_mail
	,member_belong
	,member_join_dt
	,member_join_type
	,member_st
)
VALUES(
	 (select concat('M',IFNULL(LPAD(MAX(SUBSTRING(member_idx,2,LENGTH(member_idx))) + 1,4,'0'),'0000')) from tb_member a)
	,#{memberId}
	,#{memberPw}
	,#{memberNm}
	,#{memberAddr}
	,#{memberTel}
	,#{memberHp}
	,#{memberMail}
	,#{memberBelong}
	,CURRENT_TIMESTAMP
	,#{memberJoinType}
	,'N'
)

mapper.update:
    UPDATE tb_member_auth
	SET 
		member_auth = #{memberAuth}
	WHERE member_idx = #{memberIdx}
