mapper.select:
SELECT resour_faci_id AS facilityId, facility_nm AS facilityNm, facility_loc AS facilityLoc, facility_scale AS facilityScale, facility_cost AS facilityCost, facility_equip AS facilityEquip, facility_img AS facilityImg, facility_st_cd AS facilitySt FROM tb_facility WHERE 1=1
AND facility_nm LIKE CONCAT('%',#{searchText},'%') OR facility_loc LIKE CONCAT('%',#{searchText},'%') OR facility_scale LIKE CONCAT('%',#{searchText},'%') OR facility_equip LIKE CONCAT('%',#{searchText},'%')
AND facility_nm LIKE CONCAT('%',#{searchText},'%')
AND facility_loc LIKE CONCAT('%',#{searchText},'%')
AND facility_mng_dept_cd = #{mngDeptType}
ORDER BY resour_faci_id DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO tb_facility (
	 resour_faci_id
	,facility_nm
	,facility_loc
	,facility_scale
	,facility_cost
	,facility_equip
	,facility_img
	,facility_st_cd
	,facility_mng_dept_cd
	,facility_func_cd
	,reg_dt
	,reg_id
)
VALUES(
	 (select concat('F',IFNULL(LPAD(MAX(SUBSTRING(resour_faci_id,2,LENGTH(resour_faci_id))) + 1,9,'0'),'000000000')) from tb_facility a)
	,#{facilityNm}
	,#{facilityLoc}
	,#{facilityScale}
	,#{facilityCost}
	,#{facilityEquip}
	,#{facilityImg}
	,#{facilitySt}
	,#{facilityMngDeptCd}
	,#{facilityFuncCd}
	,CURRENT_TIMESTAMP
	,'admin'
)

mapper.update:
	UPDATE tb_facility
	    SET  facility_nm = #{facilityNm}
	          ,facility_loc = #{facilityLoc}
	          ,facility_scale = #{facilityScale}
	          ,facility_cost = #{facilityCost}
	          ,facility_equip = #{facilityEquip}
	          ,facility_img = #{facilityImg}
	          ,facility_st_cd = #{facilitySt}
	          ,facility_mng_dept_cd = #{facilityMngDeptCd}
	          ,facility_func_cd = #{facilityFuncCd}
    WHERE resour_faci_id = #{facilityId}

mapper.select:
SELECT resour_faci_id AS resourceId, resource_nm AS resourceNm, resource_img AS resourceImg, (select common_nm from ta_common where group_cd = 'G00011' and common_cd = tb_resource.resource_st_cd) as resourceSt, date_format(reg_dt, '%Y.%m.%d') AS resourceRegDt FROM tb_resource WHERE 1=1
AND resource_nm LIKE CONCAT('%',#{searchText},'%')
AND resource_st_cd = #{searchType}
ORDER BY reg_dt DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO tb_resource (
	 resour_faci_id
	,resource_nm
	,resource_mng_dept_cd
	,resource_img
	,resource_st_cd
	,reg_dt
	,reg_id
)
VALUES(
	 (select concat('R',IFNULL(LPAD(MAX(SUBSTRING(resour_faci_id,2,LENGTH(resour_faci_id))) + 1,9,'0'),'000000000')) from tb_resource a)
	,#{resourceNm}
	,#{resourceMngDeptCd}
	,#{resourceImg}
	,#{resourceSt}
	,CURRENT_TIMESTAMP
	,'admin'
)

mapper.update:
UPDATE tb_resource
SET resource_nm = #{resourceNm}
     ,resource_mng_dept_cd = #{resourceMngDeptCd}
	 ,resource_img = #{resourceImg}
	 ,resource_st_cd = #{resourceSt}
WHERE resour_faci_id = #{resourceId}

			UPDATE tb_resour_faci_applicate
			SET applic_st_cd = #{applicStCd}

			      	,use_fr_dt = date_format(#{useFrDt}, '%Y%m%d')


			      	,use_to_tm = #{useFrTm}


			      	,use_to_dt = date_format(#{useToDt}, '%Y%m%d')


			      	,use_to_tm = #{useToTm}


                      ,resour_faci_id = #{facilityNm}

			WHERE appli_no = #{appliNo}
